import React, { useState } from 'react'

const Form = () => {

  const [userInfo, setUserInfo] = useState({
    name: '',
    age: ''
  })


  const addName = (e) => {
    // e.preventDefault();
    setUserInfo((oldUserInfo) => {
      return {
        ...oldUserInfo,
        name: e.target.value
      }
    })
  }

  const addAge = (e) => {
    // e.preventDefault();
    setUserInfo((oldUserInfo) => {
      return {
        ...oldUserInfo,
        age: e.target.value
      }
    })
  }

  const captureInfo = (e) => {
    e.preventDefault()
    console.log(userInfo.name + ', ' + userInfo.age);
    userInfo({
      name: userInfo.name,
      age
    })

  }

  return (
    <form onSubmit={captureInfo}>
      <label>Name</label>
      <input type="text" onChange={addName}></input>
      <label>Age (Years)</label>
      <input type="text" onChange={addAge}></input>
      <button>Add User</button>
    </form>
  )
}

export default Form
