import React, { useState } from 'react'

const Form = () => {

  const [userInfo, setUserInfo] = useState({
    name: '',
    age: ''
  })


  const addInfo = (e) => {
    // e.preventDefault();
    setUserInfo((oldUserInfo) => {
      return {
        name: e.target.input,
        age: e.input.value
      }
    })
  }

  return (
    <form >
      <label>Name</label>
      <input type="text" onChange={addInfo}></input>
      <label>Age (Years)</label>
      <input type="text" onChange={addInfo}></input>
      <button>Add User</button>
    </form>
  )
}

export default Form
