import React from 'react'
import styles from './User.module.css'

const NewBodyInput = () => {

  const [updatedNotes, setUpdatedNotes] = React.useState([])

  const onModify = (e) => {
    console.log(e.currentTarget.value);
    // const input = e.currentTarget.value;
    setUpdatedNotes((oldNotes) => {
      return [...oldNotes,
              {
                [e.target.name]: e.currentTarget.value,
                [e.target.body]: e.currentTarget.value
              }]
    })
  }


  return (
    <div className={styles['body-modify-input']}>
      <form>
        <label>Update title</label>
        <input type="text" name="title" value={updatedNotes.title} onChange={onModify}></input>
        <label>Update content</label>
        <input type="text" name="body" value={updatedNotes.body} onChange={onModify}></input>
        <div>
          <button type='submit'>Update Note</button>
          <button type='submit'>Cancel</button>
        </div>
      </form>
    </div>
  )
}

export default NewBodyInput
