function App() {

  const [userInfo, setUserInfo] = useState(
    [
    {name: 'Jeff', age: 32},
  {name: 'Mahatma', age: 16}, {name: 'Parushti', age: 54}])
  // why did i make a boolean?
  // const [populated, setPopulated] = useState(false)

  const finalUserInfo = (info) => {
    setUserInfo((oldUserInfo) => {
      return [...oldUserInfo, info]
    })
  }


  return (
    <div className=''>
      <SearchBar userList={userInfo}/>
      <UserBox finalUserInfo={finalUserInfo}/>
      <UserCard userData={userInfo}/>
    </div>
  );
}

export default App;
