import React, { useState, useEffect } from 'react'
import './MainNote.css'


const MainNote = ({ selectedNote, userData, onModification, finalUserInfo }) => {

  const [note, setNote] = useState(/*{id: '', name: '', age: ''}*/selectedNote)

  // when selectedNote changes (held in App.js), if selectedNote exists, make that 'note'
  useEffect(() => {
    if (selectedNote) setNote(selectedNote)
  }, [selectedNote])

  const mainNoteChange = (e) => {
    let newInput = e.target.value;
    // setTimeout(() => {
    //   console.log('500 ms down');
    // }, 500)

    // when note changes, set state of 'note' with new input
    setNote((oldNote) => ({ ...oldNote, age: newInput }))
 }

//  on saving, if selectedNote exists, modify 'note', otherwise create it
 const onSave = () => {
   selectedNote ? onModification(note) : finalUserInfo(note)
 }

  return (
    <div className='main-note-area'>

      {/* Make it so that if no note is currently chosen, it will save as a new one */}
      <button type='button' onClick={onSave}>{selectedNote ? 'Update' : 'Create'}</button>
      <textarea className='textarea-note' onChange={mainNoteChange} value={note.age}></textarea>
    </div>
  )
}

export default MainNote

// Need to set the note default as the first on the list
// If there's no notes existing,

// if note exists, make selectedNote array[0] by default
// else, selectedNote will be blank
