import React, { useState } from 'react';
import UserBox from './UserBox';
import UserCard from './UserCard'
import SearchBar from './SearchBar'
import Wrapper from './Helpers/Wrapper';

function App() {

  const [userInfo, setUserInfo] = useState(
    [
      {name: 'Jeff', age: 32},
      {name: 'Hank', age: 16},
      {name: 'Mahatma', age: 54},
      {name: 'Mahandas', age: 41}
    ]
  )
  const [filteredUsers, setFilteredUsers] = useState([])


  const finalUserInfo = (info) => {
    setUserInfo((oldUserInfo) => {
      return [...oldUserInfo, info]
    })
  }

  const filteredList = (filtered) => {
    console.log(filtered);
    setFilteredUsers(filtered)
  }

  const removePerson = (user) => {
    console.log(user);
    setFilteredUsers((oldUsers) => {
      return oldUsers.remove
    })
  }

  return (
    <div className='main'>
      <SearchBar userList={userInfo} filteredList={filteredList}/>
      <UserBox finalUserInfo={finalUserInfo}/>
      <UserCard userData={userInfo} filteredUsers={filteredUsers} onRemovePerson={removePerson}/>
    </div>
  );
}

export default App;
