import React, { useState } from 'react';
import UserBox from './UserBox';
import UserCard from './UserCard'
// import SearchBar from './SearchBar'
import styles from './User.module.css'
import TitlesList from './TitlesList';
import Header from './Header'
import MainNote from './MainNote';

function App() {

  const [userInfo, setUserInfo] = useState(
    [
      {id: 1, name: 'Jeff', age: 32},
      {id: 2, name: 'Hank', age: 16},
      {id: 3, name: 'Mahatma', age: 54},
      {id: 4, name: 'Mahandas', age: 41}
    ]
  )

  const [query, setQuery] = useState('')

  const [noteToDisplay, setNoteToDisplay] = useState({id: '', name: '', age: ''})

  // filtered users list
  const filteredUsers = userInfo.filter((item) => {
    return item['name'].toLowerCase().indexOf(query.toLowerCase()) !== -1
  })


  const finalUserInfo = (info) => {
    // const usersLength = userInfo.length;
    // console.log(userInfo[userInfo.length - 1].id + 1);
    setUserInfo((oldUserInfo) => {
      return [...oldUserInfo,
              { id: userInfo.length > 0 ? userInfo[userInfo.length - 1].id + 1 : 1,
                name: info.name,
                age: info.age
              }
            ]
    })
  }
  // console.log(userInfo);

  const removePerson = (user) => {
    // why is user that is passed up rendering with a completely different ID?
    // console.log(user);
    // console.log(filteredUsers);
    setUserInfo((oldFilteredUsers) => {
      const updatedUsers = oldFilteredUsers.filter((person) => {
        return person.id !== user.id
        // return person.age !== user.age && person.name !== user.name
      })
      return updatedUsers
    })
  }

  const onModification = (mod, newInput) => {
    // console.log(mod);
    setUserInfo((oldUserInfo) => {
      const updatedInfo = oldUserInfo.map((oldItem) => {
        return oldItem.id === mod.id ? {...oldItem, name: mod.title, age: mod.body} : oldItem
      })
      return updatedInfo
    })
  }

  const noteClicked = (note) => {
    const clickedNote = userInfo.filter((oneNote) => {
      return note.id === oneNote.id
    })
    console.log(clickedNote[0]);
    // const theClickedNote = {name: clickedNote[0].name, age: clickedNote[0].age, id: clickedNote[0].id}
    setNoteToDisplay({id: clickedNote[0].id, name: clickedNote[0].name, age: clickedNote[0].age})
  }

  return (
    <div className='main'>
      <Header query={query} setQuery={setQuery}/>
      <div className='search-add-container'>
        {/* <SearchBar query={query} setQuery={setQuery} /> */}
        <UserBox finalUserInfo={finalUserInfo}/>
      </div>
      <div className={styles['card-container']}>
        {/* <UserCard userData={userInfo} filteredUsers={filteredUsers} onModification={onModification} onRemovePerson={removePerson}/> */}
        <TitlesList noteClicked={noteClicked} userData={userInfo} filteredUsers={filteredUsers} onModification={onModification} onRemovePerson={removePerson}/>
        <MainNote noteInfo={noteToDisplay} userData={userInfo} filteredUsers={filteredUsers} onModification={onModification} onRemovePerson={removePerson}/>
      </div>
    </div>
  );
}

export default App;
