  finalUserInfo(newNote)
 }

  // any time noteDisplay is updated, feed it back to App
  useEffect(() => {
    //onModification(noteDisplay)
  }, [])


  return (
    <div className='main-note-area'>

      {/* Make it so that if no note is currently chosen, it will save as a new one */}
      <button type='button' onClick={onSave}>Save</button>
      <textarea className='textarea-note' onChange={mainNoteChange} value={newNote.age}></textarea>
    </div>
  )
}

export default MainNote
