import React, { useState } from 'react';
import Title from './Title.js'
import Wrapper from './Helpers/Wrapper.js';
import styles from './User.module.css'

const TitlesList = ({ onRemovePerson, onModification, filteredUsers}) => {

  const onHandleClick = (user) => {
    // console.log(user);
    onRemovePerson(user)
  }

  const moddedData = (mod) => {
    // console.log(mod);
    onModification(mod)
  }

  const filteredUserInfo = filteredUsers.map((user) => {
    // console.log(user.name + ': ' + user.age + ' (in UserCard)');
    return <Title
                key={`${Math.floor(Math.random() * user.age)}${user.name[0]}${Math.floor(Math.random() * user.age)}`}
                id={user.id}
                // id={filteredUserLength}
                name={user.name}
                // age={user.age}
                onHandleClick={onHandleClick}
                handleModify={moddedData}
                // listOfUsers={filteredUserInfo}
          />
  })


  return (
    <div style={{display: 'flex', flexDirection: 'column'}}>
      {filteredUserInfo}

    </div>
  )
}

export default TitlesList
