import React, { useState } from 'react'
import Button from './Button'
import styles from './User.module.css'
import classes from './Button.module.css'
import NewInput from './NewInput'

const User = ({ onHandleClick, handleModify, name, age }) => {

  const [modifiable, setModifiable] = useState(false)

  const removeUser = (e) => {
    // console.log(e.currentTarget);
    onHandleClick({
      name: name,
      age: age
    })
  }

  const toggleModify = () => {
    setModifiable((oldModifiable) => {
      return !oldModifiable
    })
  }

  return (
    <div className={styles['note-container']}>
      <div className={styles['name-container']}>
        <div className={styles.name}>{modifiable ? <NewInput /> : name}</div>
        <Button onClick={removeUser} className={classes.remove}> Remove</Button>
        <div onClick={toggleModify}>Modify</div>
      </div>
      <div className={styles.body}>{modifiable ? <NewInput/> : `${age} years old}`}</div>
    </div>
  )
}

export default User
