import React, { useState } from 'react'

const Form = (props) => {

  const [userInfo, setUserInfo] = useState({
    name: '',
    age: ''
    // // https://learnetto.com/blog/react-form-validation
    // formErrors: {name: '', age: ''},
    // nameValid: false,
    // ageValid: false,
    // formValid: false
    // // -----------------------------------------------
  })

  const [buttonOn, setButtonOn] = useState(false)


  const addName = (e) => {
    // e.preventDefault();
    setUserInfo((oldUserInfo) => {
      return {
        ...oldUserInfo,
        name: e.target.value
      }
    })
  }

  const addAge = (e) => {
    // e.preventDefault();
    setUserInfo((oldUserInfo) => {
      return {
        ...oldUserInfo,
        age: e.target.value
      }
    })
  }

  const validateAge = () => {
    // check the age held in state when you submit
    // if not greater than zero, don't submit
    if (userInfo.age > 0) {
      setButtonOn(true)
      // captureInfo()
    } else {
      setButtonOn(false)
    }
  }

  const captureInfo = (e) => {
    e.preventDefault()
    // console.log(userInfo.name + ', ' + userInfo.age);
    // validateAge()
    setButtonOn((oldButtonOn) => {
      return !oldButtonOn
    })

    console.log('capture info:' + buttonOn);

    // if buttonOn state is true, submit the form
    if (buttonOn) {
      // this is to pass the information up
      props.userInfo({
        name: userInfo.name,
        age: userInfo.age
      })
      setUserInfo(() => {
        return {
          name: '',
          age: ''
        }
      })
    } else {
      console.log('form not valid')
      // console.log(buttonOn);
    }
  }

  // To make controlled component, add 'value' to input field and equal it to state

  return (
    <form onSubmit={captureInfo}>
      <label htmlFor='name'>Name</label>
      <input id="name" value={userInfo.name} type="text" onChange={addName}></input>
      <label htmlFor='age'>Age (Years)</label>
      <input id="age" value={userInfo.age} type="number" onChange={addAge}></input>
      <button type="submit">Add User</button>
    </form>
  )
}

export default Form
