import React, { useState } from 'react'
import './UserBox.css'
import ErrorModal from './UI/ErrorModal'
import Button from './Button'

const Form = (props) => {

  const [userInfo, setUserInfo] = useState({
    name: '',
    age: ''
  })

  // const [buttonOn, setButtonOn] = useState(false)


  const addName = (e) => {
    // e.preventDefault();
    setUserInfo((oldUserInfo) => {
      return {
        ...oldUserInfo,
        name: e.target.value
      }
    })
  }

  const addAge = (e) => {

    setUserInfo((oldUserInfo) => {
      return {
        ...oldUserInfo,
        age: e.target.value
      }
    })
  }


  const captureInfo = (e) => {
    e.preventDefault()

    if (userInfo.name.trim().length < 1 || userInfo.age.trim().length < 1) {
      console.log('nothing entered');
      return
    }

    // adding '+' in front of variable turns it into number
    if (+userInfo.age < 0) {
      console.log('age must be 0 or over');
      return
    }

    props.userInfo({
      name: userInfo.name,
      age: userInfo.age
    })
    setUserInfo(() => {
      return {
        name: '',
        age: ''
      }
    })
  }

  // To make controlled component, add 'value' to input field and equal it to state

  return (
    <div>
      <ErrorModal title="An error occured" message="Something went wrong"/>
      <form onSubmit={captureInfo}>
        <label htmlFor='name' className='input'>Name</label>
        <input id="name" value={userInfo.name} type="text" className='input' onChange={addName}></input>
        <label htmlFor='age' className='input'>Age (Years)</label>
        <input id="age" value={userInfo.age} type="number" className='input' onChange={addAge}></input>
        <button type="submit">Add User</button>
        <Button/>
      </form>
    </div>
  )
}

export default Form
