import React, { useState } from 'react';
import Title from './Title.js'

const TitlesList = ({ onRemovePerson, onModification, noteClicked, filteredUsers}) => {

  const onHandleClick = (user) => {
    // console.log(user);
    onRemovePerson(user)
  }

  const moddedData = (mod) => {
    // console.log(mod);
    onModification(mod)
  }

  const onNoteClick = (user) => {
    // console.log(user);
    noteClicked(user)
  }

  const filteredUserInfo = filteredUsers.map((user) => {
    // console.log(user.name + ': ' + user.age + ' (in UserCard)');

    const bodyLength = user.body.split(' ')[0].length;
    const titleChar = user.title[0];
    const bodyChar = user.body[0]

    const uniqueKey = `${Math.floor(Math.random() * bodyLength)}${titleChar}${Math.floor(Math.random() * bodyLength)}`


    return <Title
                key={`${Math.floor(Math.random() * user.body)}${user.title[0]}${Math.floor(Math.random() * user.body)}`}
                id={user.id}
                // id={filteredUserLength}
                title={user.title}
                // age={user.age}
                onHandleClick={onHandleClick}
                handleModify={moddedData}
                onNoteClick={onNoteClick}
                // listOfUsers={filteredUserInfo}
          />
  })


  return (
    <div style={{display: 'flex', flexDirection: 'column', width: '25%', borderRight: '2px solid black'}}>
      {filteredUserInfo}
    </div>
  )
}

export default TitlesList
