import React, { useEffect, useState } from 'react'
import './SearchBar.css'

const SearchBar = ({ userList, filteredList, query, setQuery }) => {

  // const [filteredList, setFilteredList] = useState(props.userList)


  const filterBySearch = (e) => {

      const input  = e.target.value
      // Access input value
      setQuery(input)
      // console.log(query);
      let updatedList = [...userList]

      // if (query.trim().length === 0) {
      //   setQuery('')
      // }

      updatedList = updatedList.filter((item) => {
        return item['name'].toLowerCase().indexOf(input.toLowerCase()) !== -1
      })
      // passing this updatedList to be rendered
      filteredList(updatedList)
      // but what about if the string is empty
      // maybe write an if indexOf === -1? then render original list
  }

  return (
    <div className='search-container'>
      <label htmlFor='search'><b>Search Users</b></label>
      <input id='search' className="input" value={query} type="text" onChange={filterBySearch}/>
    </div>
  )
}

export default SearchBar
