import React, { useState } from 'react';
import User from './User.js'
import Wrapper from './Helpers/Wrapper.js';

const UserCard = (props) => {

  const onHandleClick = (user) => {
    // console.log(user);
    // return an object
    props.onRemovePerson(user)
  }

  const filteredUserInfo = props.filteredUsers.map((user, index) => {
    // console.log(user.name + ': ' + user.age + ' (in UserCard)');
    return <User key={index}
                name={user.name}
                age={user.age}
                onHandleClick={onHandleClick}
          />
  })

  const allUserInfo = props.userData.map((user, index) => {
    return <User key={index}
                  name={user.name}
                  age={user.age}
                  onHandleClick={onHandleClick}
            />
  })


  return (
    <Wrapper class>
      {/* {filteredUserInfo} */}
      {filteredUserInfo.length === 0 ? allUserInfo : filteredUserInfo}
    </Wrapper>
  )
}

export default UserCard
