import React, { useState } from 'react';
import UserBox from './UserBox';
import UserCard from './UserCard'
import SearchBar from './SearchBar'

function App() {

  const [userInfo, setUserInfo] = useState(
    [
      {name: 'Jeff', age: 32},
      {name: 'Mahatma', age: 16},
      {name: 'Parushti', age: 54}
    ]
  )
  // why did i make a boolean?
  // const [populated, setPopulated] = useState(false)

  const finalUserInfo = (info) => {
    setUserInfo((oldUserInfo) => {
      return [...oldUserInfo, info]
    })
  }

  const filteredList = (filtered) => {
    setUserInfo(filtered)
    console.log(filtered);
  }

  return (
    <div className=''>
      <SearchBar userList={userInfo} filteredList={filteredList}/>
      <UserBox finalUserInfo={finalUserInfo}/>
      <UserCard userData={userInfo}/>
    </div>
  );
}

export default App;
