import React, { useState } from 'react'

const SearchBar = (props) => {

  // const [filteredList, setFilteredList] = new useState(props.userList)
  const [filteredList, setFilteredList] = useState(props.userList)

  const filterBySearch = (e) => {
    // Access input value
    const query = e.target.value
    console.log(e.target.value);
    let updatedList = [...props.userList]
    console.log(updatedList);


    updatedList = updatedList.filter((item) => {
      return item.name.toLowerCase().indexOf(query.toLowerCase()) !== -1;
    })


    updatedList = updatedList.filter


    setFilteredList(updatedList)
    console.log('filtered list :' + filteredList);
  }

  return (
    <div>
      <label htmlFor='search'>Search users</label>
      <input id='search' type="text" onChange={filterBySearch}/>

      <div>
        <ul>
          {filteredList.map((user, index) => {
            return <li key={index}>{user.name}, {user.age} </li>
          })}
        </ul>
      </div>
    </div>
  )
}

export default SearchBar
