import React, { useState, useEffect } from 'react'
import './MainNote.css'


const MainNote = ({ noteInfo, userData, onModification }) => {

  const [noteDisplay, setNoteDisplay] = useState({id: '', name: '', age: ''})

  // const removeUser = (e) => {
  //   // console.log(e.currentTarget);
  //   onHandleClick({
  //     id: id,
  //     name: name,
  //     age: age
  //   })
  // }

  useEffect(() => {
    setNoteDisplay({id: noteInfo.id, name: noteInfo.name, age: noteInfo.age})
    // return () => {
    //   second
    // }
  }, [noteInfo])

  const mainNoteChange = (e) => {
    // console.log(e.target.value);
    let newInput = e.target.value;

    // we don't want it to update state on every keystroke - just after some keystroke inactivity and a validation
    // once it updates, it should be fed back into state in APP
    // setTimeout(() => {
    //   console.log('500 ms down');
    // }, 500)

    console.log(userData);
    setNoteDisplay((oldNote) => {
      return {
        ...oldNote,
        age: newInput
      }
    })

  }

  // any time noteDisplay) is set, feed it back to App
  useEffect(() => {
    onModification(noteDisplay)
  }, [noteDisplay])


  return (
    // <div className={styles['notes-main-screen']}>
    //   {!modifiable && <div className={styles['titles-list']}>
    //     {<div className={styles.name}>{name}</div>}
    //     {/* {<div>{`Key: ${id}`}</div>} */}
    //     {<Button onClick={removeUser} className={classes.remove}>Remove</Button>}
    //     {<div onClick={toggleModify}>Modify</div>}
    //   </div>}
    //   <div className={styles.body}>{modifiable && <NewBodyInput id={id} onModData={onModData}/>}</div>
    //   {/* <div>{age}</div> */}
    // </div>
    <div className='main-note-area'>
      {/* {noteDisplay.name} */}
      {/* <div>{noteClicked.age}</div> */}
      {/* Make it so that if no note is currently chosen, it will save as a new one */}
      <textarea className='textarea-note' onChange={mainNoteChange} value={noteDisplay.age}></textarea>
    </div>
  )
}

export default MainNote
