import React, { useState } from 'react'
import './SearchBar.css'

const SearchBar = (props) => {

  // const [filteredList, setFilteredList] = useState(props.userList)
  const [query, setQuery] = useState('')

  const filterBySearch = (e) => {
      // Access input value
      setQuery(e.target.value)
      // console.log(query);
      let updatedList = [...props.userList]

      const someThingToFilter = updatedList.filter((item) => {
        return item['name'].toLowerCase().indexOf(query.toLowerCase()) !== -1
      })
      // passing this updatedList to be rendered
      props.filteredList(updatedList)
      // but what about if the string is empty
      // maybe write an if indexOf === -1? then render original list

  }

  return (
    <div className='search-container'>
      <label htmlFor='search'>Search users</label>
      <input id='search' className="input" value={query} type="text" onChange={filterBySearch}/>
    </div>
  )
}

export default SearchBar
